    /**
     * can mark
     *
     * @param string $compareDateTime
     * @return bool
     */
    public function canMark(string $compareDateTime): bool
    {
        // get date in utc
        $dateInUTC = getSystemDateInUTC();
        // convert today date to UTC
        $dateInUTCObj = new DateTime($dateInUTC, new DateTimeZone("UTC"));
        // convert copmpare date to UTC
        $compareDateTimeObj = new DateTime($compareDateTime, new DateTimeZone("UTC"));
        // apply difference
        $diff = $dateInUTCObj->diff($compareDateTimeObj);
        // if diference is off one minute
        return $diff->format("%i") >= 1;
    }

    /**
     * get duration between dates in seconds
     *
     * @param string $d1
     * @param string $d2
     * @return array seconds
     */
    public function getDurationInMinutes(string $d1, string $d2): int
    {
        // convert d1 to UTC
        $d1Obj = new DateTime($d1, new DateTimeZone("UTC"));
        // convert d1 to UTC
        $d2Obj = new DateTime($d2, new DateTimeZone("UTC"));
        // apply difference
        $diff = $d2Obj->diff($d1Obj);
        // if diference is off one minute
        return
            $diff->s
            + ($diff->i * 60)
            + ($diff->h * 3600)
            + ($diff->d * 86400)
            + ($diff->m * 2592000)
            + ($diff->y * 31536000);
    }

    /**
     * get the dates within start and end date
     *
     * @param string $startDate
     * @param string $endDate
     * @return array
     */
    public function getDatePoolInRange(string $startDate, string $endDate): array
    {
        // convert d1 to UTC
        $d1Obj = new DateTime($startDate);
        // convert d1 to UTC
        $d2Obj = new DateTime($endDate);
        // set empty date pool
        $datesPool = [];
        //
        while ($d1Obj <= $d2Obj) {
            // add the date to the pool
            $datesPool[$d1Obj->format(DB_DATE)] = 0;
            // add X number of days in todays date
            $d1Obj->modify("+1 day");
        }
        //
        return $datesPool;
    }

    /**
     * get the dates within start and end date
     *
     * @param string $seconds
     * @param bool $doFormat Optional
     * @return array
     */
    public function convertSecondsToHours(string $seconds, bool $doFormat = false): array
    {
        // set the object
        $obj = [
            "hours" => (float)number_format($seconds / 3600, 1),
            "minutes" => (float)(($seconds % 3600) / 60),
            "seconds" => (float)($seconds % 60),
        ];
        //
        if ($doFormat) {
            return $obj["hours"];
        }
        //
        return $obj;
    }
}
