INSERT INTO `email_templates` (`sid`, `name`, `group`, `from_name`, `from_email`, `cc`, `subject`, `file`, `text`, `user_defined`, `template_code`, `status`) VALUES
(3, 'User Registration Email for Admin - AHR', 'user', 'AutomotoHR.com', 'accounts@automotohr.com', '', 'New Employer Registration', '', '<p>Dear Admin,</p>\r\n\r\n<p><strong>New User \"{{username}}\" has just registered at {{site_url}}</strong></p>\r\n\r\n<p>User ID: {{employer_id}}</p>\r\n\r\n<hr />\r\n<p>To edit details of this user click <a href=\"{{site_url}}manage_admin/employers/edit_employer/{{employer_id}}\" target=\"_blank\">here</a></p>\r\n\r\n<p> </p>\r\n\r\n<p> </p>', 0, NULL, 1),
(48, 'Welcome Email for Employer - AHR', 'user', 'AutomotoHR.com', 'accounts@automotohr.com', '', 'Welcome to Automotohr.com', '', '<p>Dear {{firstname}} {{lastname}},<br />\r\n<br />\r\nThank you for registering at {{site_url}}. </p>\r\n\r\n<p>Use the following access details to <a href=\"https://www.automotohr.com//login/\" target=\"_blank\">sign in</a> to your Career site:</p>\r\n\r\n<p>Username: {{username}}</p>\r\n\r\n<p>Password: {{password}}</p>\r\n\r\n<p><strong>One of our Team Members will be contacting you shortly to activate your account, opening a whole new world of opportunities to:</strong> </p>\r\n\r\n<p><strong>1</strong>- Build your Company Career pages in minutes<br />\r\n<strong>2</strong>- Post to all of your preferred job boards<br />\r\n<strong>3</strong>- Hire on the go with your mobile optimized hiring system<br />\r\n<strong>4</strong>- Easily evaluate candidates and collaborate with teams to make the best hire<br />\r\n<strong>5</strong>- Maintain all of your Companies pertinent and legally required hiring information, government compliance forms and data all in one place, with a single secure log in.<br />\r\n<strong>6</strong>- Complete easily managed Onboarding process.</p>\r\n\r\n<p>We are so glad you have chosen to be a part of AutomotoHR.com.<br />\r\nWe are here to help you Succeed!!<br />\r\nPlease Email or Call me directly any time with your questions or comments.<br />\r\nI would love to hear from you.<br />\r\nThank You<br />\r\n<strong>Steven Warner </strong>   <a href=\"mailto:Steven@AutomotoSocial.com\">Steven@AutomotoHR.com</a>   <br />\r\n<strong>Founder / CEO</strong><br />\r\nPhone; 951-385-8204<br />\r\n<br />\r\nAutomotoHR.com  <img alt=\"\" src=\"http://automotosocial.com/files/userfiles/images/Steven at RAA(1).jpg\" /><br />\r\n </p>\r\n\r\n<table border=\"0\" cellpadding=\"0\" cellspacing=\"0\">\r\n <tbody>\r\n  <tr>\r\n   <td>You are receiving this email to confirm an action you have taken on AutomotoHR.com. <strong><a href=\"mailto:Accounts@AutomotoHR.com?subject=Unsubscribe&body=Please remove my information from any further mailings\" tabindex=\"-1\" target=\"_parent\">Unsubscribe</a></strong></td>\r\n  </tr>\r\n  <tr>\r\n   <td>If you have registered at AutomotoHR.com and do not wish to recieve further email confirmations you can go to your profile and click the box for \"Don&#39;t Send Mailings\" </td>\r\n  </tr>\r\n  <tr>\r\n   <td>If you need assistance or have questions, please contact AutomotoHR.com<strong><a href=\"mailto:support@automotohr.com?subject=AutomotoHR Customer Service Message\"> Customer Service</a></strong><a href=\"http://automotosocial.com/contact/\">.</a><br />\r\n    </td>\r\n  </tr>\r\n </tbody>\r\n</table>', 0, NULL, 1),
(18, 'Apply On Job Email To Admin -  AHR', 'listing', '{{company_name}}', 'accounts@automotohr.com', '', 'New Job Application at {{company_name}}, JobTitle: {{original_job_title}}', '', '<p>Dear Administrator,</p>\r\n\r\n<p><br />\r\nA Candidate has applied to the following position:<br />\r\n<strong>{{job_title}}</strong><br />\r\n<br />\r\n<strong>Applicant Details:<br />\r\n<br />\r\nName: </strong>{{firstname}} {{lastname}}<br />\r\n<strong>Email: </strong>{{email}}<br />\r\n<strong>Phone Number: </strong>{{phone_number}}<br />\r\n<strong>City: </strong>{{city}}</p>\r\n\r\n<p>{{applicant_profile_link}}<br /><br />\r\n{{resume_link}}<br />\r\n<br />\r\n<strong>Note:</strong> Visit your application tracking system in AutomotoHR for more details.</p>\r\n\r\n<p><strong><a href=\"https://www.automotohr.com/login\">https://www.automotohr.com/login</a></strong></p>', 0, NULL, 1),
(4, 'Company de-Activation Email', 'user', 'AutomotoHR.com', 'accounts@automotohr.com', '', 'Activate your company at AutmotoHR', '', '<p>Dear {{firstname}} {{lastname}},</p>\r\n\r\n<p>Your company account on AutomotoHR.com was deactivated due to an expiration date limit.</p>\r\n\r\n<p>To reactivate your company account please <u><a href=\"{{site_url}}account_activation/{{activation_key}}\" target=\"_blank\">click here</a></u>.</p>\r\n\r\n<p> </p>\r\n\r\n<p>For any further enquiries<strong><em> </em></strong>please email us at Accounts@AutomotoHr.com</p>', 0, NULL, 1),
(305, 'Accurate Background Activation Document Request - AHR', 'other', 'AutomotoHR.com', 'accounts@automotohr.com', '', 'Accurate Background Activation Document Request', NULL, '<p>Please send the document to AutomotoHR!</p>', 1, NULL, 1),
(36, 'Password Change Email - AHR', 'other', 'AutomotoHR.com', 'accounts@automotohr.com', '', 'Password Recovery', '', '<p>Dear {{username}},<br />\r\n<br />\r\nYou can change your password by following the link below:<br />\r\nYour username is: {{username}}<br />\r\n<u><a href=\"{{site_url}}change_password/{{username}}/{{verification_key}}\" target=\"_blank\">Change your password</a></u></p>', 0, NULL, 1),
(38, 'Password Change Success Email - AHR', 'other', 'AutomotoHR.com', 'accounts@automotohr.com', '', 'Password Changed Successfully', '', '<p><br />\r\nYour Password has been successfully Updated.<br />\r\nplease login using this <a href=\"{{site_url}}login\">Link</a>.</p>\r\n\r\n<p> </p>\r\n\r\n<p>We are glad you have chosen to be a part of Automotohr.com.</p>\r\n\r\n<p>Please visit us often.</p>\r\n\r\n<p>Automotohr.com is a dynamic environment, with many changes and updates happening every day.</p>\r\n\r\n<p> </p>\r\n\r\n<p>We are here to help you Succeed!!</p>\r\n\r\n<p>Please Email or Call us any time with questions or comments.</p>\r\n\r\n<p>We would love to hear from you.</p>\r\n\r\n<p>Thank You</p>', 0, NULL, 1),
(300, 'Invoice Email - AHR', 'other', 'AutomotoHR.com', 'accounts@automotohr.com', '', 'Invoice Notification', '', '<p>Dear {{firstname}} ,</p>\r\n\r\n<p>Please be notified that an invoice has been generated for you on {{date}}.<br />\r\nInvoice #: {{invoice_id}}</p>\r\n\r\n<p><br />\r\n<strong>Invoice Description:</strong></p>\r\n\r\n<p>{{invoice_description}}</p>\r\n\r\n<p><br />\r\n<strong>Invoice </strong><strong>Items:</strong><br />\r\n{{product_list}}</p>\r\n\r\n<p><br />\r\n<br />\r\n----------------------------------------</p>\r\n\r\n<p>Sub-total: {{invoice_subtotal}}</p>\r\n\r\n<p>Discount: {{discount}}</p>\r\n\r\n<p>Special discount: {{special_discount}}</p>\r\n\r\n<p>Total: {{invoice_total}}</p>', 0, NULL, 1),
(306, 'Accurate Background Activation Document To Accurate Background Team - AHR', 'other', 'AutomotoHR.com', 'accounts@automotohr.com', '', 'Accurate Background Activation Document', NULL, '<p>Please see the attachment.</p>', 1, NULL, 1),
(307, 'CSV Employer Import', 'user', 'AutomotoHR.com', 'accounts@automotohr.com', '', 'Welcome to AutomotoHR', NULL, '<p>Dear {{firstname}} {{lastname}},</p>\r\n\r\n<p>Welcome Aboard {{company_name}}</p>\r\n\r\n<p>Please Click on the following link and update your Login Credentials:</p>\r\n\r\n<p><strong><u><a href=\"{{site_url}}employee_registration/{{verification_key}}\" target=\"_blank\">Update Your Login Credentials</a></u></strong></p>\r\n\r\n<p>Thanks & Best Regards</p>\r\n\r\n<p>Human Resource Department</p>', 1, NULL, 1),
(308, 'Refer a job', 'listing', 'AutomotoHR.com', 'accounts@automotohr.com', '', '{{username}} has referred a job, that you might be interested in!', NULL, '<p>Dear {{referred_to}},</p>\r\n\r\n<p>{{username}} thought you might be interested in this position and has refered the following Job: <strong>{{job_title}}</strong></p>\r\n\r\n<p>Please Click on the following link to view the Details:</p>\r\n\r\n<p><strong><u><a href=\"http://{{sub_domain_url}}/job_details/{{job_id}}\" target=\"_blank\">{{job_title}}</a></u></strong></p>\r\n\r\n<p>Thanks & Best Regards</p>\r\n\r\n<p>Human Resource Department</p>\r\n\r\n<p> </p>', 1, NULL, 1),
(309, 'Deluxe Theme Activation Request To Admin', 'other', 'AutomotoHR.com', 'accounts@automotohr.com', '', 'Deluxe Theme Activation Request', NULL, '<p>Dear Admin,</p>\r\n\r\n<p>You have received a Company Deluxe Theme Activation Request.</p>\r\n\r\n<p>This email is generated when an employer who currently has the Standard theme activated and clicks on the Request to upgrade button on the Deluxe theme. Contact this employer to help them to upgrade to the Deluxe theme. </p>\r\n\r\n<p>The Account expiration of this company is not set, Either set an expiration date for the company or activate the Deluxe Theme from admin panel.</p>\r\n\r\n<p> </p>\r\n\r\n<p>Company Details:</p>\r\n\r\n<p> </p>\r\n\r\n<p><strong>Company ID: </strong>{{company_id}}</p>\r\n\r\n<p><strong>Company Name: </strong>{{company_name}}</p>\r\n\r\n<p>---------------------------------------------------------</p>\r\n\r\n<p><strong>Automated Email; Please Do Not reply!</strong></p>\r\n\r\n<p>---------------------------------------------------------</p>', 1, NULL, 1),
(311, 'HR Document Notification', 'other', 'AutomotoHR.com', 'accounts@automotohr.com', '', 'HR Documents', NULL, '<p>Dear {{username}},</p>\r\n\r\n<p><em><strong>Congratulations and Welcome to  </strong></em><strong>{{company_name}}</strong></p>\r\n\r\n<p>We have attached a few documents to get you up and running for your first day.</p>\r\n\r\n<p>Please complete these documents by clicking on the link below.</p>\r\n\r\n<p>To manage all of your documents please go to <a href=\"{{baseurl}}received_documents/{{verification_key}}\">This Link</a>.</p>\r\n\r\n<p>Or</p>\r\n\r\n<p>To manage all of your documents please click on <a href=\"{{baseurl}}login\">Login</a> to your AutomotoHR account..</p>\r\n\r\n<p>Go to your AutomotoHR Dashboard > My Profile > My Hr Documents.</p>\r\n\r\n<p><em>If you have any questions at all, please feel free to send us a note at any time and we will get back to you as quickly as we can.</em></p>\r\n\r\n<p><em>We are excited and look forward to you joining our team!</em></p>\r\n\r\n<p><strong>The HR Team at  {{company_name}}</strong></p>\r\n\r\n<p> </p>\r\n\r\n<p>---------------------------------------------------------</p>\r\n\r\n<p><strong>Automated Email; Please Do Not reply!</strong></p>\r\n\r\n<p>---------------------------------------------------------</p>\r\n\r\n<p> </p>\r\n\r\n<p> </p>', 1, NULL, 1),
(312, 'HR Document Notification Without User Name', 'other', 'AutomotoHR.com', 'accounts@automotohr.com', '', 'HR Documents', NULL, '<p>To manage all of your documents please go to <a href=\"{{baseurl}}received_documents/{{verification_key}}\">This Link</a>.</p>\r\n\r\n<p>Or</p>\r\n\r\n<p><a href=\"{{baseurl}}login\">Login</a> to your AutomotoHR account.</p>\r\n\r\n<p>Goto your AutomotoHR Dashboard > My Profile > My Hr Documents.</p>\r\n\r\n<p>Hr Administrator</p>\r\n\r\n<p>{{company_name}}</p>\r\n\r\n<p> </p>\r\n\r\n<p>---------------------------------------------------------</p>\r\n\r\n<p><strong>Automated Email; Please Do Not reply!</strong></p>\r\n\r\n<p>---------------------------------------------------------</p>\r\n\r\n<p> </p>\r\n\r\n<p> </p>', 1, NULL, 1),
(313, 'Account Expiration Notification', 'other', 'AutomotoHR.com', 'accounts@automotohr.com', '', 'Your {{company_name}} Account Expires In {{remaining_days}} Day(s)', NULL, '<p> {{company_name}},</p>\r\n\r\n<p>Your subscription with AutomotoHr.com will expire in {{remaining_days}} day(s).</p>\r\n\r\n<p>To avoid a disruption in your service, please log in to your account on AutomotoHR.com and renew your subscription at your earliest convenience.</p>\r\n\r\n<p>Sincerly,</p>\r\n\r\n<p>The AutomotoHr Team.</p>\r\n\r\n<p> </p>\r\n\r\n<p><strong>**This is an automated email please do not reply**</strong></p>\r\n\r\n<p> </p>\r\n\r\n<p> </p>', 1, NULL, 1),
(314, 'Job Listing Status Notification', 'listing', 'AutomotoHr', 'accounts@automotohr.com', '', '{{job-title}} - Status has been updated', NULL, '<p>Dear {{user-name}},</p>\r\n\r\n<p>The Job status for <strong>{{job-title}}</strong> has been updated by <strong>{{admin-name}}</strong>.</p>\r\n\r\n<p>Please check your Jobs List for details.</p>\r\n\r\n<p><a href=\"https://www.automotohr.com/login\">https://www.automotohr.com/login</a></p>\r\n\r\n<p>AutomotoHr</p>\r\n\r\n<p><strong>**This is an automated email please do not reply.**</strong></p>', 1, NULL, 1),
(315, 'New Listing Notification To User With Approval Rights', 'listing', 'AutomotoHr', 'accounts@automotohr.com', '', 'New Job Listing - {{job-title}}', NULL, '<p>Dear {{user-name}}</p>\r\n\r\n<p>The Following is a new Job Listing added by <strong>{{creator-name}}</strong>.</p>\r\n\r\n<p><strong>{{job-title}}</strong></p>\r\n\r\n<p>Please review and approve the listing at your earliest convenience.</p>\r\n\r\n<p>AutomotoHr</p>\r\n\r\n<p><strong>**This is an automatic email please do not reply.**</strong></p>', 1, NULL, 1),
(316, 'Job Listing Share To Employees', 'listing', 'AutomotoHr', 'accounts@automotohr.com', '', 'We Have A Great New Job Listing To Share With You - {{job-title}}', NULL, '<p>Dear {{employee-name}},</p>\r\n\r\n<p>There is a new job Opportunity at  {{company-name}}.</p>\r\n\r\n<p>If you know of a great person, that you would be proud to work with and think would be a great fit for this position, please share the following link with them:</p>\r\n\r\n<p>{{job-link}}</p>\r\n\r\n<p>{{job-ad}}</p>\r\n\r\n<p>{{share-links}}</p>\r\n\r\n<p>Share this position with your Social networks by clicking the links above.</p>\r\n\r\n<p>{{company-name}}</p>\r\n\r\n<p><strong>**This is an automated email please do not reply**</strong></p>', 1, NULL, 1),
(317, 'Recurring Payments Authorization Form Notification To Super Admin', 'other', 'AutomotoHr', 'accounts@automotohr.com', '', 'AutomotoHR - Recurring Payments Authorization Request', NULL, '<p>Dear Admin,</p>\r\n\r\n<p>A new recurring payments authorization request has been sent to {{company_name}}&#39;s Admin email address {{company_admin_email}}.</p>\r\n\r\n<p>**This is an automated email, please do not reply.**</p>', 1, NULL, 1),
(318, 'Recurring Payments Authorization Form Notification To Company Admin', 'other', 'AutomotoHr', 'accounts@automotohr.com', '', 'AutomotoHR - Recurring Payments Authorization Request', NULL, '<p>Dear {{company_admin}},</p>\r\n\r\n<p>Please click on the following link to complete our {{form_link}} and to authorize recurring payments.</p>\r\n\r\n<p>Thank you.</p>\r\n\r\n<p>The AutomotoHR Team</p>\r\n\r\n<p>**This is an automated email, please do not reply.**</p>', 1, NULL, 1),
(319, 'End User License Agreement Notification Super Admin', 'other', 'AutomotoHr', 'accounts@automotohr.com', '', 'AutomotoHR - End User License Agreement', NULL, '<p>Dear Admin,</p>\r\n\r\n<p>End User License Agreement has been successfully sent to {{company_name}}. on email address {{company_admin_email}}.</p>\r\n\r\n<p>{{form_link}}</p>\r\n\r\n<p>Thanks</p>\r\n\r\n<p>AutomotoHR</p>\r\n\r\n<p>**This is an automoted email, please do not reply**</p>', 1, NULL, 1),
(320, 'End User License Agreement Notification Company Admin', 'other', 'AutomotoHr', 'accounts@automotohr.com', '', 'AutomotoHR - End User License Agreement', NULL, '<p>Dear {{company_admin}},</p>\r\n\r\n<p>Please click on the blue link below and complete the<strong> </strong>AutomotoSocial LLC/AutomotoHR <em><strong>\"End User License Agreement Form\".</strong></em></p>\r\n\r\n<p>{{form_link}}</p>\r\n\r\n<p>Thank you,</p>\r\n\r\n<p>The AutomotoHR Team</p>\r\n\r\n<p> </p>\r\n\r\n<p><strong>**This is an automotic email, please do not reply. **</strong></p>', 1, NULL, 1),
(321, 'Credit Card Authorization Form Notification Super Admin', 'other', 'AutomotoHr', 'accounts@automotohr.com', '', 'AutomotoHR - Credit Card Authorization Form Notification', NULL, '<p>Dear Admin,</p>\r\n\r\n<p>A Credit Card Authorization Form has been sent via email to {{company_name}} on email address {{company_admin_email}}.</p>\r\n\r\n<p>A Direct Link to the Form is {{form_link}}.</p>\r\n\r\n<p>Thank You,</p>\r\n\r\n<p>AutomotoHR</p>\r\n\r\n<p>**This is an automated email, please do not reply**</p>', 1, NULL, 1),
(322, 'Credit Card Authorization Form Notification Company Admin', 'other', 'AutomotoHr', 'accounts@automotohr.com', '', 'AutomotoHR - Credit Card Authorization Form Notification', NULL, '<p>Dear {{company_admin}},</p>\r\n\r\n<p>Please click on the following link to complete our Credit Card Authorization Form.</p>\r\n\r\n<p>{{form_link}}</p>\r\n\r\n<p>Thank you,</p>\r\n\r\n<p>The AutomotoHR Team,</p>\r\n\r\n<p><strong>**This is an automated email, please do not reply.**</strong></p>', 1, NULL, 1),
(324, 'New Applicant Hiring Approval Request', 'other', 'AutomotoHr', 'accounts@automotohr.com', '', 'New Applicant Hiring Approval Request for {{job_title}}', NULL, '<p>Dear {{approving_authority}},</p>\r\n\r\n<p>{{employer_name}} has requested a new hiring approval for {{applicant_name}} for Job {{job_title}}.</p>\r\n\r\n<p>Please Log in to your account and review the candidate.</p>\r\n\r\n<p><strong><a href=\"https://www.automotohr.com/login\"><span [removed]>https://www.automotohr.com/login</span></a></strong></p>\r\n\r\n<p>Thank you,</p>\r\n\r\n<p>AutomotoHR</p>\r\n\r\n<p>** This is a an automated email, please do not reply **</p>', 1, NULL, 1),
(325, 'Applicant Hiring Approval Status Update', 'other', 'AutomotoHr', 'accounts@automotohr.com', '', 'AutomotoHR - Applicant {{approval_status}}', NULL, '<p>Dear {{employer_name}},</p>\r\n\r\n<p>{{approving_authority}} has {{approval_status}} the candidate {{applicant_name}}.</p>\r\n\r\n<p>Thank you.</p>\r\n\r\n<p>AutomotoHR</p>\r\n\r\n<p>** This is an automated email please do not reply **</p>', 1, NULL, 1),
(326, 'Forms Notification To Admin', 'other', '{{company_name}}', 'accounts@automotohr.com', '', '{{company_name}} - Documents', NULL, '<p>Dear Administrator,</p>\r\n\r\n<p>An email containing the following links have been sent for {{company_name}} to {{email_address}}.</p>\r\n\r\n<p>{{links}}</p>\r\n\r\n<p>Thank you,</p>\r\n\r\n<p>AutomotoHr,</p>\r\n\r\n<p> </p>\r\n\r\n<p><strong>** This is an automated email please do not reply **</strong></p>', 1, NULL, 1),
(327, 'Forms Notification To Client', 'other', 'AutomotoHr', 'accounts@automotohr.com', '', 'AutomotoHr - Documents', NULL, '<p>Dear {{company_name}} Administrator,</p>\r\n\r\n<p>Congratulations on your choice of AutomotoHR to revolutionize and transform your HR, Recruiting and On-boarding into a High-Powered Employment Brand Marketing and Sales Machine.</p>\r\n\r\n<p>Please click on each of the following links below and complete the forms to start the activation process for your account with AutomotoHR.</p>\r\n\r\n<p>{{links}}</p>\r\n\r\n<p>{{message}}</p>\r\n\r\n<p>Thank you for your business,</p>\r\n\r\n<p>The AutomotoHR Team</p>\r\n\r\n<p> </p>\r\n\r\n<p> </p>\r\n\r\n<p><strong>** This is an automated email please do not reply **</strong></p>\r\n\r\n<p> </p>\r\n\r\n<p> </p>', 1, NULL, 1),
(328, 'Admin Invoice Payment Notification', 'other', 'AutomotoHr', 'accounts@automotohr.com', '', 'Payment Successfully Processed for Invoice {{invoice_number}}', NULL, '<p>Dear {{company_admin}},</p>\r\n\r\n<p>A payment for Invoice # {{invoice_number}} has been successfully processed, details are as follows:</p>\r\n\r\n<p>Payment Method : {{payment_method}}</p>\r\n\r\n<p>Payment Date : {{payment_date}}</p>\r\n\r\n<p>{{invoice}}</p>\r\n\r\n<p> </p>\r\n\r\n<p>Accounts Department,</p>\r\n\r\n<p>The AutomotoHR Team</p>\r\n\r\n<p> </p>\r\n\r\n<p><strong>** This is an automated email, please do not reply **</strong></p>\r\n\r\n<p> </p>\r\n\r\n<p> </p>', 1, NULL, 1),
