(330, 'Notification to Employer on New Account Creation from Super Admin', 'other', 'AutomotoHr', 'accounts@automotohr.com', '', 'Welcome to AutomotoHR', NULL, '<p>Dear {{employer_name}},</p>\r\n\r\n<p>You have been registered as a <strong>{{access_level}}</strong> in <strong>{{company_name}}</strong> on http://www.automotohr.com.</p>\r\n\r\n<p>Use the following access details to sign in to the site:</p>\r\n\r\n<p>{{login_page}}</p>\r\n\r\n<p>User Name : {{username}}</p>\r\n\r\n<p>Password : {{password}}</p>\r\n\r\n<p>Regards,</p>\r\n\r\n<p>The AutomotoHR Team</p>\r\n\r\n<p> </p>\r\n\r\n<p><strong>** This is an automated email, please do not reply. **</strong></p>\r\n\r\n<p> </p>\r\n\r\n<p> </p>', 1, NULL, 1),
(331, 'On-boarding Request', 'other', 'AutomotoHr', 'accounts@automotohr.com', '', 'On-boarding Request', NULL, '<p>Congratulations {{applicant_name}},</p>\r\n\r\n<p>You are one step closer to becoming a welcome member of our awesome team.</p>\r\n\r\n<p>Please Click on the blue buton below, to set up an account and complete the application process through our HR compliance and On-boarding system.</p>\r\n\r\n<p>This is time sensitive, so please complete it as soon as possible.</p>\r\n\r\n<p>{{on_boarding_link}}</p>\r\n\r\n<p>Thank You</p>\r\n\r\n<p> </p>\r\n\r\n<p> </p>\r\n\r\n<p>---------------------------------------------------------</p>\r\n\r\n<p><strong>Automated Email, Please Do Not reply.</strong></p>\r\n\r\n<p>---------------------------------------------------------</p>', 1, NULL, 1),
(332, 'Applicant Hiring Conditional Rejection Notification', 'other', 'AutomotoHr', 'accounts@automotohr.com', '', 'AutomotoHR - Applicant Conditionally Rejected', NULL, '<p>Dear {{employer_name}},</p>\r\n\r\n<p>{{approving_authority}} has Conditionally Rejected the candidate {{applicant_name}}, with the following conditions.</p>\r\n\r\n<p> </p>\r\n\r\n<p>{{conditions}}</p>\r\n\r\n<p> </p>\r\n\r\n<p> </p>\r\n\r\n<p>Please remedy these conditions and resubmit \"{{applicant_name}}\" again for a candidate review and approval.</p>\r\n\r\n<p> </p>\r\n\r\n<p>AutomotoHR</p>\r\n\r\n<p> </p>\r\n\r\n<p><strong>**This is an automated email please do not reply**</strong></p>\r\n\r\n<p> </p>\r\n\r\n<p> </p>', 1, NULL, 1),
(333, 'Full Employment Application Request', 'other', '{{company_name}}', 'accounts@automotohr.com', '', '{{company_name}} - Full Employment Application', NULL, '<p>Dear {{user_name}},</p>\r\n\r\n<p>{{company_name}} has sent you a new document link.</p>\r\n\r\n<p>Please click on the following link and complete the \"Full Employment Application\".</p>\r\n\r\n<p>{{link}}</p>\r\n\r\n<p>Thank you,</p>\r\n\r\n<p>{{company_name}}</p>\r\n\r\n<p> </p>\r\n\r\n<p><strong>**This is an automated email, please do not reply**</strong></p>\r\n\r\n<p> </p>\r\n\r\n<p> </p>', 1, NULL, 1),
(334, 'JOB LISTING SHARE TO EMAIL ADDRESS', 'listing', 'AutomotoHR', 'accounts@automotohr.com', '', 'We Have A Great New Job Listing To Share With You - {{job-title}}', NULL, '<p>Dear {{employee-name}},</p>\r\n\r\n<p>There is a new job Opportunity at  {{company-name}}.</p>\r\n\r\n<p>Please click on the following link to view the details:</p>\r\n\r\n<p>{{job-link}}</p>\r\n\r\n<p>{{job-ad}}</p>\r\n\r\n<p>{{share-links}}</p>\r\n\r\n<p>Share this position with your Social networks by clicking the links above.</p>\r\n\r\n<p>{{company-name}}</p>\r\n\r\n<p><strong>**This is an automated email please do not reply**</strong></p>', 1, NULL, 1),
(335, 'Credit Card Expiration Auto Notification', 'other', 'AutomotoHR', 'accounts@automotohr.com', '', 'AutomotoHr - Credit Card Expiration Notification', NULL, '<p>Dear {{billing_contact_name}},</p>\r\n\r\n<p>We&#39;re writing to remind you that the following credit or debit card will expire soon. If your card expires, the card issuer (bank or other financial institution) may decline payment for your account charges. If payment is declined, your AutomotoHR/Career platform for the account will be <em>Paused</em>.</p>\r\n\r\n<p>A credit card saved for {{company_name}} is expiring at the end this month.</p>\r\n\r\n<p>Credit Card Details are as follows :</p>\r\n\r\n<p>Credit Card Number : {{card_number}}</p>\r\n\r\n<p>Name on Credit Card : {{name_on_card}}</p>\r\n\r\n<p>Expiration Month: {{expiration_month}}</p>\r\n\r\n<p>Expiration Year: {{expiration_year}}</p>\r\n\r\n<p> </p>\r\n\r\n<p>We request that you update your credit card information as soon as possible.</p>\r\n\r\n<p>To update your payment information, visit the <a href=\"https://www.automotohr.com/cc_management\">Credit Card Management</a> page(<a href=\"https://www.automotohr.com/cc_management\">https://www.automotohr.com/cc_management</a>) which can be found from the Settings page on your AutomotoHR account. When you click this link you may need to sign in to your AutomotoHR account first.</p>\r\n\r\n<p>On the Credit Card Management page, you can edit your card expiration date (click <strong>Edit</strong> next to the card information) or add a new card. If you add a new card, you&#39;ll need to set it as the current \"Default\" payment method after you add the card info.</p>\r\n\r\n<p>Thank you for your continued business,</p>\r\n\r\n<p>The AutomotoHr Team</p>\r\n\r\n<p>P.S</p>\r\n\r\n<p>If you need help with this or any other issue, please feel free to contact us. We will always be very happy to talk with you.</p>\r\n\r\n<p> </p>\r\n\r\n<p><strong>**This is an automated email, please do not reply**</strong></p>', 1, NULL, 1),
(336, 'Turnover Cost Calculation Report', 'other', 'AutomotoHR', 'accounts@automotohr.com', '', 'Here is your Turnover Cost Calculation Report for - {{store_name}}', NULL, '<p>Dear {{client_name}},</p>\r\n\r\n<p>Here is your Turnover Cost Calculation Report for {{dealership_name}}.</p>\r\n\r\n<p>To view the report please {{turnover_cost_calculation_report_link}}</p>\r\n\r\n<p>If you would like a free demonstration on how AutomotoHR can help you to create a proven process to solve your turnover probelm and return $$ back to your bottom line</p>\r\n\r\n<p>Please click here   <strong><a href=\"https://www.automotohr.com/schedule_your_free_demo\">https://www.automotohr.com/schedule_your_free_demo</a></strong></p>\r\n\r\n<p>or</p>\r\n\r\n<p>Feel free to contact us anytime at the numbers below.</p>\r\n\r\n<p>Team {{store_name}} </p>', 1, NULL, 1),
(337, 'Previously Applied From Indeed Notification', 'other', '{{company_name}}', 'accounts@automotohr.com', '', '{{company_name}} - You have previously applied for {{job_title}}', NULL, '<p>Dear {{applicant_name}},</p>\r\n\r\n<p>You have previously applied for \"{{job_title}}\" and your job application is in our system. </p>\r\n\r\n<p>We are reviewing applications currently and expect to schedule interviews in the next couple of weeks.</p>\r\n\r\n<p>If you are selected for the next phase of the recruitment process, you will be contacted by our Human Resources staff for an interview session.</p>\r\n\r\n<p>Thank you, again, for your interest in {{company_name}}</p>\r\n\r\n<p> </p>\r\n\r\n<p><strong>**This is an automated email, please do not reply**</strong></p>\r\n\r\n<p> </p>\r\n\r\n<p> </p>', 1, NULL, 1),
(338, 'Video Interview Questionnaires', 'other', '{{company_name}}', 'accounts@automotohr.com', '', '{{company_name}} - Video Interview Questionnaires', NULL, '<p><strong>Congratulations</strong> {{applicant_name}},</p>\r\n\r\n<p>You are one step closer to becoming a welcome member of our awesome team.</p>\r\n\r\n<p>A Video Interview Questionnaire request has been sent to you.</p>\r\n\r\n<p>To complete your Video Interview it will be necessary, for you to use a computer or mobile device with a camera and audio preferably running Google Chrome.</p>\r\n\r\n<p><strong>Please Note:</strong><br />\r\nIf you are unable to get the Video Interview to record on your mobile device, it may be necessary to complete the recording using a desktop/laptop computer. You will need a Webcam and Flash player</p>\r\n\r\n<p>This link is time sensitive, so please complete it as soon as possible.</p>\r\n\r\n<p><strong>Please complete the Video Interview questions by clicking on the link below:</strong></p>\r\n\r\n<p>{{url}}</p>\r\n\r\n<p>Thank you for your interest in {{company_name}}.</p>\r\n\r\n<p> </p>\r\n\r\n<p><strong>**This is an automated email, please do not reply**</strong></p>\r\n\r\n<p> </p>\r\n\r\n<p> </p>', 1, NULL, 1),
