(340, 'Over-Due Invoices Detail', 'other', 'Automoto HR', 'accounts@automotohr.com', '', 'Automoto HR Due Invoices for: {{company_name}}', NULL, '<p>Dear {{client_name}},</p>\r\n\r\n<p> Listed below are the <strong>Overdue Invoices</strong> for your <strong>{{company_name}}</strong> account,</p>\r\n\r\n<p>You can pay these invoices by visiting the <strong>\"Overdue Invoices\"</strong> link from the Settings Page. </p>\r\n\r\n<p>Please {{payment_page_link}} to pay this invoice online now.</p>\r\n\r\n<p>Details are as follows :</p>\r\n\r\n<p>{{invoices_detail}}</p>\r\n\r\n<p>Thanks and Best Regards,</p>\r\n\r\n<p>The AutomotoHR Team</p>', 1, NULL, 1),
(341, 'Update Credit Card Notification', 'other', 'Automoto HR', 'accounts@automotohr.com', '', 'AutomotoHR - Request to Update Credit Card Details', NULL, '<p>Dear {{contact_name}},</p>\r\n\r\n<p>This email is to notify you that the Credit Card Registered for your {{company_name}} account has expired or is not working.</p>\r\n\r\n<p>Credit Card Details are as follows :</p>\r\n\r\n<p>Credit Card Number : {{card_number}}</p>\r\n\r\n<p>Name on Credit Card : {{name_on_card}}</p>\r\n\r\n<p>Expiration Month: {{expiration_month}}</p>\r\n\r\n<p>Expiration Year: {{expiration_year}}</p>\r\n\r\n<p>Please update your Credit Card Information by loging in to your AutomotoHr Account and navigating to the</p>\r\n\r\n<p>Settings Page > Credit Card Management Panel</p>\r\n\r\n<p>or by directly visiting {{cc_management_link}} you may need to sign in to your AutomotoHR account. </p>\r\n\r\n<p>Thank you for your continued business,</p>\r\n\r\n<p>The AutomotoHR Team</p>\r\n\r\n<p>P.S</p>\r\n\r\n<p>If you need help with this or any other issue, please feel free to contact us. We will always be very happy to talk with you.</p>\r\n\r\n<p>AutomotoHR Support: (888) 794-0794 ext 2 <br />\r\n<strong><a href=\"mailto:support@automotohr.com\"><span [removed]>Support@AutomotoHR.com</span></a></strong></p>\r\n\r\n<p> </p>\r\n\r\n<p> </p>', 1, NULL, 1),
