(354, 'Send Candidates info', 'alerts', '{{company_name}}', 'info@automotohr.com', '', 'Here are Candidate(s) that were sent to you for your Review', NULL, '<p>{{block_start}}</p>\r\n\r\n<p><em><strong>Job Applied to: </strong></em></p>\r\n\r\n<p><strong>{{job_title}}</strong></p>\r\n\r\n<p><em><strong>Applicant Details:</strong></em></p>\r\n\r\n<p><strong><em>Name: </em>{{applicant_name}}</strong></p>\r\n\r\n<p><em><strong>Email:</strong></em> {{email}}</p>\r\n\r\n<p><em><strong>Phone Number:</strong></em> {{phone_number}}</p>\r\n\r\n<p><em><strong>City:</strong></em> {{city}}</p>\r\n\r\n<p><strong><em>Profile Link:</em></strong> {{link}}</p>\r\n\r\n<p><strong>{{download_resume}}</strong></p>\r\n\r\n<p>{{block_end}}</p>\r\n\r\n<p><strong>Note:</strong> Visit your application tracking system in AutomotoHR for more details.</p>\r\n\r\n<p><strong>https://www.automotohr.com/login</strong></p>', 1, NULL, 1),
(355, 'Calendar Event Notifiation', 'other', 'AutomotoHr', 'dev@automotohr.com', '', '{{event_type}} Notification - {{company_name}}', NULL, '<p>Dear {{participant_name}},</p>\r\n\r\n<p>You have {{event_type}} scheduled today at {{start_time}}. It will start in {{duration}} minutes.</p>\r\n\r\n<p><b>Event Details</b></p>\r\n\r\n<p>Title: {{event_title}}</p>', 1, NULL, 1),
(356, 'Affiliate Program Send Login Request', 'user', 'AutomotoHR.com', 'accounts@automotohr.com', '', 'Your new AutomotoHR Affiliate Program login information', '', '<p>Dear {{affiliate_name}},</p>\r\n\r\n<p><strong>The Team at AutomotoHR would like to welcome and introduce you to your new AutomotoHR Talent management and hiring platform Affiliate Program.​</strong></p>\r\n\r\n<p> </p>\r\n\r\n<p><strong>Your Username: {{username}}</strong></p>\r\n\r\n<p><strong>Use the following link to {{create_password_link}}</strong></p>\r\n\r\n<p> </p>\r\n\r\n<p><strong>Your account will be activated and ready to use, as soon as you have created your new password, opening a whole new world of opportunities:</strong> </p>\r\n\r\n<p>Here is a link for you to log in  <a href=\"https://www.automotohr.com/affiliate_portal/login\"><strong>https://www.automotohr.com/affiliate_portal/login</strong></a>  </p>\r\n\r\n<p><br />\r\nWe are here to help you Succeed!!<br />\r\nPlease Email or Call us any time with your questions or comments.<br />\r\nWe would love to hear from you.<br />\r\nThank You</p>\r\n\r\n<p><strong>The AutomotoHR Team </strong>   <br />\r\nAutomotoHR.com</p>\r\n\r\n<h5><strong>Customer Service And Technical Support</strong></h5>\r\n\r\n<ul>\r\n <li>(888) 794-0794 ext 2</li>\r\n <li><u><a href=\"mailto:Support@AutomotoHR.com\"> Support@AutomotoHR.com</a></u></li>\r\n</ul>\r\n\r\n<p> </p>\r\n\r\n<p> </p>\r\n\r\n<table border=\"0\" cellpadding=\"0\" cellspacing=\"0\">\r\n <tbody>\r\n  <tr>\r\n   <td>You are receiving this email to confirm an action you have taken on AutomotoHR.com. <strong><a href=\"mailto:Accounts@AutomotoHR.com?subject=Unsubscribe&body=Please remove my information from any further mailings\" tabindex=\"-1\" target=\"_parent\">Unsubscribe</a></strong></td>\r\n  </tr>\r\n  <tr>\r\n   <td> </td>\r\n  </tr>\r\n  <tr>\r\n   <td>If you need assistance or have questions, please contact AutomotoHR.com  <strong><a href=\"mailto:support@automotohr.com?subject=Aut