(357, 'Job Listing Auto Expiration', 'other', 'AutomotoHR.com', 'info@automotohr.com', '', 'Job automatically Expired for - {{company-name}}', NULL, '<p>Dear {{contact-name}},</p>\r\n\r\n<p>The Following Job with the Title {{title}} has automatically expired.</p>\r\n\r\n<p>Please log in to your account on AutomotoHR.com to review it.</p>\r\n\r\n<p>Sincerly,</p>\r\n\r\n<p>The AutomotoHr Team.</p>', 1, NULL, 1),
(358, 'Training session event', 'alerts', '{{company_name}}', 'info@automotohr.com', NULL, '{{subject}}', NULL, '<p>{{message}}</p>\r\n\r\n<p>Topic: {{session_name}}</p>\r\n\r\n<p>Description: {{session_description}}</p>\r\n\r\n<p>Location: {{session_location}}</p>\r\n\r\n<p>Date: {{session_date}}</p>\r\n\r\n<p>Start Time: {{session_start_time}}</p>\r\n\r\n<p>End Time: {{session_end_time}}</p>\r\n\r\n<p> </p>\r\n\r\n<p><strong>Note:</strong> Visit your events in AutomotoHR for more details.</p>\r\n\r\n<p><strong>https://www.automotohr.com/calendar/my_events</strong></p>', 1, NULL, 1),
(359, 'Demo Autoresponder', 'alerts', 'AutomotoHR', 'info@automotohr.com', '', 'Thank you for your interest in AutomotoHR one of our Team Members will be contacting you shortly', NULL, '<p>Dear {{name}},</p>\r\n\r\n<p>Thank you for your interest in AutomotoHR</p>\r\n\r\n<p><strong>One of our Team Members will be contacting you shortly to share, a whole new world of opportunities to:</strong> </p>\r\n\r\n<p><strong>1</strong>- Create and publish all of your jobs to the major job listing systems in just minutes<br />\r\n<strong>2</strong>- Post to all of your preferred job boards<br />\r\n<strong>3</strong>- Hire on the go with your mobile optimized hiring and on-boarding system<br />\r\n<strong>4</strong>- Easily evaluate candidates and collaborate with teams to make the best hire<br />\r\n<strong>5</strong>- Maintain all of your Companies pertinent and legally required hiring information, government compliance forms and data all in one place, with a single secure log in.<br />\r\n<strong>6</strong>- Complete and easily managed Onboarding process, and Employee Management system.</p>\r\n\r\n<p>We are so glad you have chosen to learn more about AutomotoHR.com and can&#39;t wait to talk with you.<br />\r\nWe are here to help you Succeed!!<br />\r\n<br />\r\nThe AutomotoHR Team <img alt=\"\" src=\"http://automotosocial.com/files/userfiles/images/Steven at RAA(1).jpg\" /></p>\r\n\r\n<p> </p>\r\n\r\n<p> </p>\r\n\r\n<p> </p>', 1, NULL, 1),
(360, 'Affiliate Program Autoresponder', 'alerts', 'AutomotoHR', 'info@automotohr.com', '', 'Thank you for your interest in AutomotoHR', NULL, '<p>Dear {{name}},</p>\r\n\r\n<p>Thank you for your interest in our AutomotoHR Affiliate program</p>\r\n\r\n<p><strong>One of our Team Members will be contacting you shortly to share a whole new world of opportunities</strong> </p>\r\n\r\n<p>We are so glad that you have chosen to be a part of our Success at AutomotoHR and we can&#39;t wait to talk with you.<br />\r\nWe are here to help you Succeed!!<br />\r\n<br />\r\nThe AutomotoHR Team <img alt=\"\" src=\"http://automotosocial.com/files/userfiles/images/Steven at RAA(1).jpg\" /></p>\r\n\r\n<p> </p>\r\n\r\n<p> </p>', 1, NULL, 1),
(361, 'Affiliate End User License Notification to Client', 'alerts', 'AutomotoHR', 'info@automotohr.com', '', 'AutomotoHR Affiliate Documents', NULL, '<p>Dear {{affiliate_name}},</p>\r\n\r\n<p>Please click on each of the following links below and complete the Affiliate forms to start the activation process for your account with AutomotoHR.</p>\r\n\r\n<p>{{links}}</p>\r\n\r\n<p>Thank you for your business,</p>\r\n\r\n<p>The AutomotoHR Team</p>\r\n\r\n<h5>Customer Service And Technical Support including Affiliate Support</h5>\r\n\r\n<ul>\r\n <li>(888) 794-0794 ext 2</li>\r\n <li><a href=\"mailto:Support@AutomotoHR.com\"> Support@AutomotoHR.com</a></li>\r\n</ul>', 1, NULL, 1),
(362, 'Calendar Event Reminder Notification', 'alerts', 'AutomotoHR', 'info@automotohr.com', '', '{{event_type}} Reminder - {{company_name}}', NULL, '<p>Dear {{participant_name}},</p>\r\n\r\n<p>You have {{event_type}} scheduled on {{event_date}} at {{start_time}}.</p>\r\n\r\n<b>Event Details:</b>\r\n\r\n<p>Title: {{event_title}}</p>\r\n', 1, NULL, 1);
INSERT INTO `email_templates` (`sid`, `name`, `group`, `from_name`, `from_email`, `cc`, `subject`, `file`, `text`, `user_defined`, `template_code`, `status`) VALUES
(364, 'Rejection Letter', 'portal_email_templates', NULL, NULL, NULL, 'Your Application Status at {{company_name}}', NULL, '<p>Dear {{applicant_name}}:</p><p>Thank you for your application for the position of {{job_title}} at {{company_name}}. As you can imagine, we have received a large number of applications. I am sorry to inform you that you have not been selected for an interview for this position.</p><p>The {{company_name}} selection committee thanks you for the time you invested to apply for the {{job_title}} position. We encourage you to apply for future openings.</p><p>Best wishes for a successful job search.<br />Thank you, again, for your interest in {{company_name}}.</p><p>Best,<br />{{company_name}} Hiring team</p>', 1, 'rejection_letter', 1),
(371, 'test', 'super_admin_templates', '{{company_name}}', 'info@automotohr.com', '', 'test - {{first_name}} {{last_name}}', NULL, '<ul>\r\n <li>{{first_name}}</li>\r\n <li>{{last_name}}</li>\r\n <li>{{email}}</li>\r\n <li>{{phone}}</li>\r\n</ul>\r\n\r\n<p>test. Ignore this email</p>', 1, 'test', 1),
(372, 'confirmation_email_to_creator', 'user', '{{company_name}}', 'info@automotohr.com', '', '{{event_status}} requested for the event {{event_title}}', NULL, '<p>{{template_header}}</p>\r\n\r\n<div>\r\n<p><strong>Dear {{first_name}} {{last_name}},</strong>    </p>\r\n\r\n<p><strong>{{applicant_name}} has requested to change the event status to &#39;{{event_status}}&#39;.</strong></p>\r\n\r\n<p><strong>Event Details are as follows:</strong>  </p>\r\n\r\n<p>{{event_title}}</p>\r\n\r\n<p>{{event_date}} </p>\r\n\r\n<p>{{event_start_time}}</p>\r\n\r\n<p>{{event_end_time}}</p>\r\n\r\n<p>{{event_link}}</p>\r\n\r\n<p>{{reason}}</p>\r\n\r\n<p>{{event_new_date}}</p>\r\n\r\n<p>{{event_new_start_time}}</p>\r\n\r\n<p>{{event_new_end_time}}</p>\r\n</div>\r\n\r\n<p>{{template_footer}}</p>', 1, 'confirmation-email-to-creator', 1),
(366, 'Application Acknowledgement Letter', 'portal_email_templates', NULL, NULL, NULL, 'Job Application received at {{company_name}}', NULL, '<p>Dear {{applicant_name}}:</p><p>We appreciate your interest in {{company_name}} and the position of {{job_title}} for which you applied. We are reviewing applications currently and expect to schedule interviews in the next couple of weeks. If you are selected for the next phase of the recruitment process, you will be contacted by our Human Resources staff for an interview session.<br />Thank you, again, for your interest in {{company_name}}.<br />We do appreciate the time you invested in this application.</p><p>Regards,<br />{{company_name}} Hiring team</p>', 1, 'application_acknowledgement_letter', 1),
(367, 'Talent Network Acknowledgement Letter', 'portal_email_templates', NULL, NULL, NULL, 'Talent Network request received at {{company_name}}', NULL, '<p>Dear {{applicant_name}}:</p><p>Thank you for joining our Talent Network!</p><p>We are excited that you have given us the opportunity to get to know you, and tell you more about our company.</p><p>We will only contact you with opportunities that we think might meet your requirements, or that could be a good match for you.</p><p>Please don&#39;t become disappointed, if you don&#39;t hear back from us right away. We may not have the perfect opportunity and fit available for you today but we will contact you when something great is available.</p><p>Joining our Talent Network, allows us to make you aware of game changing career opportunities. Since many talented people are already employed when they contact us, we understand the need for confidentiality and discretion while exploring other job opportunities. We keep all information in total confidence.</p><p>Regards,<br />{{company_name}} Hiring team</p>', 1, 'talent_acknowledgement_letter', 1),
(368, 'Reference Request Letter', 'portal_email_templates', NULL, NULL, NULL, 'Please Provide References', NULL, '<p>Dear {{applicant_name}},</p><p>Your Job Application for {{job_title}} has been well recieved.</p><p>Kindly click on the following link and provide \'References\' and their contact details for confirmation:</p><p>{{reference_link}}</p><p>Please contact the references you are using and let them know that they will be receiving an email reference questionnaire and possibly a call from one of our company managers. Inform your reference of the urgency of completing the questionnaire.</p><p>Thank You!<br></p><p>---------------------------------------------------------<br></p><p>Automated Email; Please Do Not reply!</p><p><br>---------------------------------------------------------</p>', 1, 'reference_request_letter', 1),
(369, 'Employment Reference Questionnaire  Letter', 'portal_email_templates', NULL, NULL, NULL, 'Employment Reference Questionnaire', NULL, '<p>Dear {{reference_name}},</p><p>{{applicant_name}} has provided your contact details as a reference for employment.</p><p>Please help {{applicant_name}} by clicking on the following link and answering a short questionnaire:</p><p>{{reference_questionnaire_link}}</p><p>Thank You!<br></p><p>---------------------------------------------------------<br></p><p>Automated Email; Please Do Not reply!</p><p><br>---------------------------------------------------------</p>', 1, 'employment_reference_questionnaire_letter', 1),
(373, 'Welcome to your new AutomotoHR Talent management, hiring , Onboarding and Employee Management platform', 'super_admin_templates', '{{company_name}}', 'info@automotohr.com', '', 'Welcome To Your New AutomotoHR Talent Management, Hiring , Onboarding And Employee Management Platform', NULL, '<p>Hi Chris</p>\r\n\r\n<p>The Team at AutomotoHR would like to welcome and introduce you to your new AutomotoHR Talent management and hiring platform.</p>\r\n\r\n<p>This is your access to your full AutomotoHR site for Gary Lang Auto Group.</p>\r\n\r\n<p>Here is a link for you to log in. <a href=\"https://www.automotohr.com/login\" rel=\"noreferrer nofollow\" target=\"_blank\">https://www.automotohr.com/login</a></p>\r\n\r\n<p>You can also find your \"Employee login” located in the footer of your Career page at<br />\r\n<a href=\"https://gary-lang-auto-group.automotohr.com/\" rel=\"noreferrer nofollow\" target=\"_blank\">https://gary-lang-auto-group.automotohr.com/</a> this is also the URL that we will use to redirect on your store websites under Employment or Careers.</p>\r\n\r\n<p>You have full access to your account and it is active now and Jobs can be added at any time.<br />\r\nIf you could please send me a list of the job postings that you would like to get running I will have our team work on them right away.<br />\r\nAlso please send me a list of your team members that you would like to give access to the system. We will need their first and last name, job title and email address and we will get them added as well.</p>\r\n\r\n<p>I would like to schedule a convenient time for your AutomotoHR training session. (We will be conducting the training through GoToMeeting)<br />\r\nPlease invite as many of your team members as you would like to use the system.<br />\r\nWe will be covering the basics and best practices to get the most from your system. Ongoing help and training is always free and available for you and your team.</p>\r\n\r\n<p>We have created your dealer career site for you. It is very easy for you to customize at any time.<br />\r\nI also verified that your jobs are listed on the major job sites. With some accounts the applicants start rolling in right away, with others it may take a bit more time.</p>\r\n\r\n<p><a href=\"https://gary-lang-auto-group.automotohr.com/\" rel=\"noreferrer nofollow\" target=\"_blank\">https://gary-lang-auto-group.automotohr.com/</a></p>\r\n\r\n<p>Please contact me directly if you have any questions and to set up your GoToMeeting training session<br />\r\nThank you<br />\r\nSteven Warner<br />\r\n<a href=\"mailto:Steven@AutomotoHR.com\">Steven@AutomotoHR.com</a><br />\r\nDirect 951-385-8204</p>\r\n\r\n<p>logo low res.png</p>\r\n\r\n<p><a href=\"https://www.automotohr.com/\" rel=\"noreferrer nofollow\" target=\"_blank\">https://www.automotohr.com</a></p>', 1, 'welcome-to-your-new-automotohr-talent-management-hiring-onboarding-and-employee-management-platform', 1),
(374, 'An Introduction to the AutomotoHR Hiring, Electronic Onboarding and Employee Management System', 'super_admin_templates', '{{company_name}}', 'info@automotohr.com', '', 'An Introduction to the AutomotoHR Hiring, Electronic Onboarding and Employee Management System', NULL, '<p>Hi Curtis</p>\r\n\r\n<p>I would love the opportunity to talk with you, and share all of the benefits of using AutomotoHR.</p>\r\n\r\n<p>With the AutomotoHR platform all of your jobs will automatically be posted to the major job listings systems like Indeed, Glassdoor, ZipRecruiter, Jobs2Careers, Google jobs and hundreds of others as both a Paid Featured listing and an Organic listing all at no extra cost to you. </p>\r\n\r\n<p>So if you are currently paying to post each of your job positions you can save all of those $$$.</p>\r\n\r\n<p>Get everything you need to target, attract and hire the top talent with a best-in-class candidate experience.</p>\r\n\r\n<p>Includes all the recruitment, HR Electronic Onboarding, Employee Management and hiring marketing capabilities you&#39;ll need. Provide a modern, Mobile Optimized user experience that your hiring managers will embrace to easily collaborate and close the best candidates. Run the entire HR platform your way.</p>\r\n\r\n<p>If it is convenient for you, I would love to schedule a GoToMeeting session to demonstrate the AutomotoHR system and answer any questions you or your team might have.</p>\r\n\r\n<p>Looking forward to talking with you</p>\r\n\r\n<p>Steven Warner</p>\r\n\r\n<p>Steven@AutomotoHR.com</p>\r\n\r\n<p>Direct 951-385-8204</p>', 1, 'an-introduction-to-the-automotohr-hiring-electronic-onboarding-and-employee-management-system', 1),
(375, 'AutomotoHR a better smarter way to Hire and Manage Great People', 'super_admin_templates', '{{company_name}}', 'info@automotohr.com', '', 'AutomotoHR a better smarter way to Hire and Manage Great People', NULL, '<table border=\"0\" cellpadding=\"0\" cellspacing=\"0\" id=\"templateHeader\">\r\n <tbody>\r\n  <tr>\r\n   <td>\r\n   <table border=\"0\" cellpadding=\"0\" cellspacing=\"0\">\r\n    <tbody>\r\n     <tr>\r\n      <td> </td>\r\n     </tr>\r\n    </tbody>\r\n    <tbody>\r\n     <tr>\r\n      <td>\r\n      <table align=\"left\" border=\"0\" cellpadding=\"0\" cellspacing=\"0\">\r\n       <tbody>\r\n        <tr>\r\n         <td>\r\n         <table border=\"0\" cellpadding=\"0\" cellspacing=\"0\">\r\n          <tbody>\r\n           <tr>\r\n            <td>\r\n            <table border=\"0\" cellpadding=\"0\" cellspacing=\"0\">\r\n             <tbody>\r\n              <tr>\r\n               <td>\r\n               <p><strong>Dear</strong>  </p>\r\n\r\n               <p><br />\r\n               <strong>We include: <br />\r\n               <br />\r\n               A Compete fully Branded Featured multi page Career sites optimized for video for each store location.<br />\r\n               <br />\r\n               Unlimited number of job postings<br />\r\n               All of your job postings will automatically be posted organically on the major job sites. </strong><strong>It’s INCLUDED</strong><br />\r\n               <br />\r\n               <u><em><strong>Something nobody else offers</strong></em></u><strong>!! <br />\r\n               All of your jobs will automatically be listed as a  FEATURED job postings on the major job sites. For a majority of our clients that means that they will NOT have to spend any additional $$ promoting their jobs. Often times Saving the company Thousands of Dollars a month! It’s INCLUDED</strong><br />\r\n               <br />\r\n               <strong>Facebook Recruiting application which posts all of your jobs to Your company Facebook page and on Facebook for each store location.  </strong><strong>It’s INCLUDED</strong><br />\r\n               <br />\r\n               <strong>An Unlimited number of your team members can be added into the system.  </strong><strong>It’s INCLUDED</strong><br />\r\n               <br />\r\n               <strong>Complete templated interviewing system to standardize and make your company interviewing process compliant.  </strong><strong>It’s INCLUDED</strong><br />\r\n               <br />\r\n               <strong>Fully automated Video Interviewing system.  </strong><strong>It’s INCLUDED</strong><br />\r\n               <br />\r\n               <strong>Complete messaging system.   </strong><strong>It’s INCLUDED</strong><br />\r\n               <br />\r\n               <strong>Complete Interview scheduling and communication system.  </strong><strong>It’s INCLUDED</strong><br />\r\n               <br />\r\n               <strong>Full Electronic Onboarding process with integrated E Signature and Documentation system.   </strong><strong>It’s INCLUDED<br />\r\n               <br />\r\n               Employee Management System with full Reporting and Mitigation for Sexual Harassment, Safety and Accident Reporting and Workplace Violence.  </strong><br />\r\n               <strong>It’s INCLUDED<br />\r\n               <br />\r\n               Training and Learning Management System.  </strong><strong>It’s INCLUDED</strong><br />\r\n               <br />\r\n               <strong>Too many feature and efficiencies to list.</strong><strong>   </strong><br />\r\n               <br />\r\n               <strong>All of the Features and processes on our platform are AutomotoHR’s own proprietary properties and we have not white labeled or added somebody else’s features and called them our own. What that means for you is, if there is something that can be improved or changed to make your life better. We do it. Plain and simple!!<br />\r\n               <br />\r\n               All upgrades, New features and added technology are all included at no extra cost to you.</strong><br />\r\n               <br />\r\n               *Background and drug screening conducted on our platform is done through Accurate background, a private company for an extra cost. We do not white label others brands and are fully transparent.<br />\r\n               <br />\r\n               <strong><a href=\"https://www.automotohr.com/\">https://www.AutomotoHR.com/</a> </strong><strong>- a better, smarter way to Hire and Manage Great Automotive People. </strong></p>\r\n               </td>\r\n              </tr>\r\n             </tbody>\r\n            </table>\r\n            </td>\r\n           </tr>\r\n          </tbody>\r\n         </table>\r\n\r\n         <p>  <br />\r\n         Available in the U.S and Canada<br />\r\n         <br />\r\n         AutomotoHR is a complete job marketing system that posts your job positions to thousands of job sites including the majors like indeed, Glassdoor, Ziprecruiter, Jobs2Careers etc at no extra cost for Featured posts and Organic listing, with a complete applicant tracking, candidate management system and Onboarding platform, all for less than what you would normally pay for one monthly job listing on Careerbuilder or Indeed. <br />\r\n         <br />\r\n         <br />\r\n         <strong>Transform Your HR and Recruiting Into a High-Powered Employment Brand Marketing and Sales Machine</strong><br />\r\n         <br />\r\n         Get everything you need to target, attract and hire the top talent with a best-in-class candidate experience. Includes all the recruitment, HR and hiring marketing capabilities you&#39;ll need. Provide a modern, state of the art mobile user experience that your hiring managers will embrace to easily collaborate and close the best candidates. Run your entire HR/Hiring platform your way. Enterprise level to Mom and Pop<br />\r\n         <br />\r\n         Additionally we have just released our very own proprietary Video interviewing system.<br />\r\n         <br />\r\n         With a complete career micro site included, to attach to your dealership or company website. <br />\r\n         (Here are a few random examples of our Career sites ) <br />\r\n         <strong><a href=\"https://paragon-honda.automotohr.com/\">https://paragon-honda.automotohr.com/</a><br />\r\n         <a href=\"http://paragon-acura.automotohr.com/\" target=\"_blank\">http://paragon-acura.automotohr.com/</a></strong> <br />\r\n         <a href=\"https://dvpeople.com/\">https://dvpeople.com/</a><br />\r\n         <a href=\"https://patriot-subaru-of-saco.automotohr.com/\">https://patriot-subaru-of-saco.automotohr.com/</a><br />\r\n         <br />\r\n         <br />\r\n         <br />\r\n         (Here is an example of one our clients Facebook Career Job listing Recruiting API that we provide to dealers/companies included in your platform)<br />\r\n         <strong><a href=\"https://www.facebook.com/ParagonAcuraNY/app/1777970655815215/\" target=\"_blank\">https://www.facebook.com/ParagonHondaNY/app/1056371161146950/</a></strong><br />\r\n         <br />\r\n         Build amazingly beautiful branded career websites and micro-sites, Delight candidates with an amazing candidate experience, Distribute your jobs to thousands of job boards, Build a kick butt employee referral engine and Talent Network, Seamlessly communicate with candidates, Schedule and manage interviews with ease, Integrate skills assessments to build a pipeline of top talent, Complete easily managed On-boarding and hiring process, Hire on the go with a mobile hiring platform, EEO & OFCCP: ensure the compliance of your processes.<br />\r\n         <br />\r\n         <br />\r\n         <br />\r\n         <strong>HOW DO THE BEST JOB CANDIDATES VIEW YOUR COMPANY?</strong></p>\r\n\r\n         <ul>\r\n          <li>Improve Your Employer Brand and Go Paperless</li>\r\n         </ul>\r\n\r\n         <ul>\r\n          <li>Hiring is not just about getting more applications, it’s about getting better ones that will make your competitors jealous.</li>\r\n         </ul>\r\n\r\n         <ul>\r\n          <li>When it comes to attracting top candidates, your employer brand influences the applicants you receive or scares away.</li>\r\n         </ul>\r\n\r\n         <ul>\r\n          <li>Of course you think your company is awesome, but how do job candidates perceive your employer brand?</li>\r\n         </ul>\r\n\r\n         <ul>\r\n          <li>Whether your career page needs a revamp or you’re just looking for new inspiration to ignite your employer brand, we’re here to help.</li>\r\n         </ul>\r\n         <strong>So, how does your employer brand shape up?</strong><br />\r\n         <br />\r\n         <br />\r\n         <strong><a href=\"https://www.automotohr.com/\">https://www.automotohr.com/</a></strong><br />\r\n         <br />\r\n         <strong>Thank you<br />\r\n         <br />\r\n         Steven Warner<br />\r\n         <br />\r\n         <a href=\"mailto:Steven@AutomotoHR.com\">Steven@AutomotoHR.com</a><br />\r\n         <br />\r\n         Direct <a href=\"tel:951-385-8204\">951-385-8204</a></strong>\r\n\r\n         <table align=\"left\" border=\"0\" cellpadding=\"0\" cellspacing=\"0\">\r\n         </table>\r\n         </td>\r\n        </tr>\r\n       </tbody>\r\n      </table>\r\n      </td>\r\n     </tr>\r\n    </tbody>\r\n   </table>\r\n   </td>\r\n  </tr>\r\n </tbody>\r\n</table>', 1, 'automotohr-whats-included', 1),
